import * as React from 'react';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';
import InfoRoundedIcon from '@mui/icons-material/InfoRounded';
import Button from '@mui/material/Button';
import Tooltip from '@mui/material/Tooltip';
import { apps } from '../../../appData/Applications';
import MetaParser from '../../../images/metaParser.svg'


export default function ApplicationGrid() {

    const renderApps = () => {
        return apps.map( (app, index) => {
            return(
                <Grid item key={index}>
                    <Paper
                        sx={{
                            p: 2,
                            margin: 'auto',
                            width: 300,
                            height:300,
                            flexGrow: 1,
                            backgroundColor: (theme) =>
                            theme.palette.mode === 'dark' ? '#1A2027' : '#f2f3f5',
                            backgroundImage: MetaParser
                        }}
                    >
                        <Grid container spacing={2}>
                            <Grid item sx={{width:'100%', height:'300px'}}>
                                <div style={{display:'flex'}}>
                                    <Tooltip title={app.info}>
                                        <InfoRoundedIcon />
                                    </Tooltip>
                                </div>
                                <div style={{width:'100%', fontWeight:'bolder', fontSize:28, textAlign:'center'}}>
                                    <p style={{margin:0}}>{app.name}</p>
                                </div>

                                <div style={{display:'flex', marginTop:210, justifyContent:'right'}}>
                                    { app.accountRequired ? 
                                        <Button variant="outlined" sx={{marginRight:1}} onClick={app.onClick}>Get Access</Button>
                                        :
                                        null
                                    }
                                    <Button variant="outlined" onClick={app.onClick}>Launch</Button>
                                </div>
                            </Grid>
                        </Grid>
                    </Paper>
                </Grid>
            )
        })
    }

    return (
        <Grid container spacing={2} sx={{justifyContent:'center', marginTop:'20px'}}>
            {renderApps()}
        </Grid>
  );
}
